package whileLoop;

import java.util.*;

public class WhileLoop8 {

    public static void main(String[] args) {
        // TODO Auto-generated method stub
        Scanner scanner = new Scanner(System.in);
        int a = scanner.nextInt();
        
        while(a > 0) {
            System.out.print(a + " ");
            a--;
        }
        
        
    }

}
